<div class="form-group row">
    <label class="col-lg-3 control-label text-lg-right pt-2">Range</label>
    <div class="col-lg-6">
        <div class="m-md slider-primary ui-slider ui-corner-all ui-slider-horizontal ui-widget ui-widget-content" data-plugin-slider="" data-plugin-options="{ &quot;half_values&quot;: true, &quot;values&quot;: [ 25, 75 ], &quot;range&quot;: true, &quot;max&quot;: 100 }" data-plugin-slider-output="#listenSlider2" data-plugin-slider-link-values-to="#priceRangeValues2">
            <input id="listenSlider2" type="hidden" value="25, 75">
            <div class="ui-slider-range ui-corner-all ui-widget-header" style="left: 25%; width: 50%;"></div><span tabindex="0" class="ui-slider-handle ui-corner-all ui-state-default" style="left: 25%;"></span><span tabindex="0" class="ui-slider-handle ui-corner-all ui-state-default" style="left: 75%;"></span></div>
        <p id="priceRangeValues2" class="price-range-values">The <code>min</code> is: <b class="min">25</b> and the <code>max</code> is: <b class="max">75</b></p>
    </div>
</div>
